CREATE FUNCTION [dbo].[CalculateMetascore]
(
	@rate int,
	@reliabilityScore int,
	@countReviews int
)
RETURNS decimal
AS

BEGIN
	declare @reviewRate int;
	declare @oldMetascore float;
	declare @reliabilityScore int;
	declare @movieId int;
	declare @sumMetascore float;
	declare @criticId int;
	declare @reviewsCount int;

	set @sumMetascore = 0;
	set @criticId = 0;
	set @reliabilityScore = 0;

	select @movieId = review.Movie_Id, 
		   @reviewRate = review.Rate, 
		   @criticId = review.Critic_Id
	from inserted review;

	select @reliabilityScore = critic.ReliabilityScore
	from Critic critic 
	where critic.Id = @criticId;

	select @oldMetascore = movie.MetaScore
	from Movie movie
	where movie.Id = @movieId;

	--nadjem broj kritika za taj film
	select @reviewsCount =  count(*) from Review where Movie_Id = @movieId;		

	select @sumMetascore += @oldMetascore + dbo.CalculateMetascore(@reviewRate, @reliabilityScore, @reviewsCount);

	update Movie set MetaScore = @sumMetascore where Id = @movieId;
END

BEGIN
	 DECLARE
		@countRows int;	

	SELECT @countRows = count(*) FROM Movie 
	WHERE Deleted = 0 and Title like '%' + @searchText + '%'

	DECLARE PROJEKAT_CURSOR CURSOR
	FOR SELECT * FROM Movie
	DECLARE
		@Id int,
		@Duration int,
		@Title nvarchar(50),
		@Desctiprion nvarchar(100),
		@Director_Id int,
		@Distributor_Id int,
		@MetaScore int,
		@Deleted bit;

BEGIN
	OPEN PROJEKAT_CURSOR;
	FETCH NEXT FROM PROJEKAT_CURSOR
	INTO @Id, @Duration, @Title, @Desctiprion, @Director_Id, @Distributor_Id, @MetaScore, @Deleted;

	WHILE @@FETCH_STATUS = 0 
	BEGIN
	IF @countRows = 0
	INSERT INTO @findedMovies (Id, Duration, Title, Desctiprion, Director_Id,
										  Distributor_Id, MetaScore, Deleted)
		SELECT Id, Duration, Title, Desctiprion, Director_Id, Distributor_Id, MetaScore, Deleted
	    FROM Movie
		WHERE Deleted = 0 and Desctiprion like '%' + @searchText + '%' and
			  Id not in(select Id from @findedMovies where Id=Id)
	ELSE
	INSERT INTO @findedMovies (Id, Duration, Title, Desctiprion, Director_Id, 
										  Distributor_Id, MetaScore, Deleted)
		SELECT Id, Duration, Title, Desctiprion, Director_Id, Distributor_Id, MetaScore, Deleted
		FROM Movie 
		WHERE Deleted = 0 and Title like '%' + @searchText + '%' and
			  Id not in(select Id from @findedMovies where Id=Id)

	FETCH NEXT FROM PROJEKAT_CURSOR
	INTO @Id, @Duration, @Title, @Desctiprion, @Director_Id, @Distributor_Id, @MetaScore, @Deleted;

	END

	CLOSE PROJEKAT_CURSOR;
	RETURN
	END
 END
